#Test constant source
Base.@kwdef mutable struct SpatialConstantSource{T} <: Source
	time :: T = 0.0
	Amp :: T = 0.01
	t0 :: T = 10.0
	tau :: T = 1.0
end

Sz(t, x, y, GS ::SpatialConstantSource) = 1/sqrt(1 + GS.Amp*(1 + tanh((t - GS.t0)/GS.tau)))
