Sz_ty(t, x, y, GS ::Quench) = 0
Sz_txx(t, x, y, GS ::Quench) = 0
Sz_tyy(t, x, y, GS ::Quench) = 0
Sz_txy(t, x, y, GS ::Quench) = 0


